import useFetch from "../../helpers/useFetch";
import { useParams, useNavigate } from 'react-router-dom'
import { MapGL } from "../../components";
import './fieldDetails.css';
import { getCurrentDate } from "../../helpers/functions";
import emailjs from 'emailjs-com';

const FieldDetails = () => {
    const { id } = useParams()
    const { data: field, isPanding, error } = useFetch('http://localhost:8000/fields/' + id)
    const navigate = useNavigate()
    const currentDate = getCurrentDate();
    const [userDate, setUserDate] = useState(currentDate);

    // const history = useHistory()

    // const handleDeleteClick = () => {
    //     fetch('http://localhost:8000/blogs/' + blog.id, {
    //         method: 'DELETE'
    //     }).then(() => {
    //         history.push('/')
    //     })
    // }

    const handleHomeClick = () => {
        navigate("/")
    }
    function orderField(){
        e.preventDefault();
        var templateParams = {
            from_name: 'James',
            field_name: field.name,
            date:userDate
        };

        emailjs.sendForm('service_k630q6m', 'template_fts276l', form.current, 'guDHMPICwhsH6_B9W')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
            e.target.reset();
    }

    return (
        <div className="field-details">
            <div className="field-details-text">
                {isPanding && <div>טוען...</div>}
                {error && <div>{error}</div>}
                {field && (
                    <field>
                        <h2>{field.name}</h2>
                        <p>כתובת {field.address}</p>
                        <br />
                        <div> לכתוב בוייז מה שצריך</div>
                        <form ref={form} onSubmit={sendEmail}>
                            <input type="date" defaultValue={currentDate} id="datePicker" name="trip-start" onChange={(event) => setUserDate(event.target.value)} />
                            <button onclick={orderField}>הזמן מגרש</button>
                        </form>
                        <button onClick={handleHomeClick}>חזור למגרשים</button>
                        {/* <button onClick={handleDeleteClick}>delete</button> // THIS DOESN'T WORK YET! */}
                    </field>
                )}
            </div>

            <div className='field-details-map'>
                {field && <MapGL fields={field} />}
            </div>
        </div>
    );
}

export default FieldDetails;
