import BlogList from './BlogList';
import MapGL from './MapGL';
import useFetch from './useFetch';
import { useState } from 'react';
// import { useHistory } from 'react-router-dom';

const Home = () => {
    const { data: fields, isPanding, error } = useFetch('http://localhost:8000/db')
    fields = Array.from(props.fields);

    const [title, setTitle] = useState('')
    const [city, setCity] = useState('')
    const [author, setAuthor] = useState('mario')
    const [isPending, setIsPending] = useState(false)
    // const history = useHistory()
    console.log('In home')
    console.log({fields})

    return ( 
        <div className="home">
            <div className="search-bar">
                <form>
                    <label>בחר עיר</label>
                    {!isPending && <button>חפש</button>}
                    {isPending && <button disabled>...מחפש</button>}
                    <select value={city}
                        onChange={(e) => setCity(e.target.value)}>
                        <option value='tel-aviv'>תל אביב</option>
                        <option value='other'>אחר</option>
                    </select>

                </form>
            </div>
          
            <div className='homepage-text'>{error && <div>{error}</div>}</div>
            <div className='homepage-text'>{isPanding && <div>...טוען</div>}</div>
            <div className='homepage-text'>{fields && <BlogList fields={fields} title='מגרשים' />}</div>
            <div className='homepage-map'><MapGL /></div>
        </div>
     );
}
 
export default Home;
