import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax

const MapGL = ({fields}) => {

    const mapContainer = useRef(null)
    const [lng, setLng] = useState(34.7838)
    const [lat, setLat] = useState(32.0711)
    const [zoom, setZoom] = useState(12.10)
    const mapGl = useRef(null)

    useEffect(() => {

    });

    useEffect(() => {
        
        // function to set center of map to a spacific field - currenly doesn't work
        if (Array.isArray(fields)) {
            console.log("In (if):") // logs for debugging
        } else {
            console.log("In (else):") // logs for debugging
            console.log("Initial lat = " + lat) // logs for debugging
            setLng(fields.coordinates[1])
            const static_lat = fields.coordinates[1]
            setLat(fields.coordinates[0])
            const static_lng = fields.coordinates[0]
            console.log("After setLat lat = " + lat) // logs for debugging
        }

        if (mapGl.current) return; // initialize map only once
        if (Array.isArray(fields)) {
            mapGl.current = new mapboxgl.Map({
                container: mapContainer.current,
                style: 'mapbox://styles/mapbox/streets-v11',
                center: [lng, lat],
                zoom: zoom
            });
        } else {
            mapGl.current = new mapboxgl.Map({
                container: mapContainer.current,
                style: 'mapbox://styles/mapbox/streets-v11',
                center: [fields.coordinates[0], fields.coordinates[1]],
                zoom: zoom
            })
        }
        if (Array.isArray(fields)) {
            fields.map((field) =>
                new mapboxgl.Marker().setLngLat(field.coordinates).addTo(mapGl.current)
            );
        } else {
            new mapboxgl.Marker().setLngLat(fields.coordinates).addTo(mapGl.current)
            setLng({ lng: fields.coordinates[1] })
            setLat({ lat: fields.coordinates[0] })
        }
        
    });

    useEffect(() => {
        if (!mapGl.current) return; // wait for map to initialize
        mapGl.current.on('move', () => {
            setLng(mapGl.current.getCenter().lng.toFixed(4));
            setLat(mapGl.current.getCenter().lat.toFixed(4));
            setZoom(mapGl.current.getZoom().toFixed(2));
        });
    });

    return ( 
        <mapgl className="mapGl">
            <div ref={mapContainer} className="map-container" />
        </mapgl>
     );
}
 
export default MapGL;
