import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax

const MapGL = ({fields}) => {

    const mapContainer = useRef(null);
    const [lng, setLng] = useState(34.7838);
    const [lat, setLat] = useState(32.0711);
    const [zoom, setZoom] = useState(12.10);
    const map = useRef(null);
    const [marker, setMarker] = useState([0,0])

    console.log(marker)

    useEffect(() => {
        if (map.current) return; // initialize map only once
        map.current = new mapboxgl.Map({
            container: mapContainer.current,
            style: 'mapbox://styles/mapbox/streets-v11',
            center: [lng, lat],
            zoom: zoom
        });

        new mapboxgl.Marker().setLngLat([34.7838, 32.0711]).addTo(map.current)
    });

    // useEffect(() => {
    //     const map = new mapboxgl.Map({
    //         container: mapContainer.current,
    //         style: 'mapbox://styles/mapbox/streets-v11',
    //         center: [lng, lat],
    //         zoom: zoom
    //     });
    // });

    useEffect(() => {
        if (!map.current) return; // wait for map to initialize
        map.current.on('move', () => {
            setLng(map.current.getCenter().lng.toFixed(4));
            setLat(map.current.getCenter().lat.toFixed(4));
            setZoom(map.current.getZoom().toFixed(2));
        });
    });

    useEffect(() => {
        {
            fields.map((field) =>
                new mapboxgl.Marker().setLngLat([34.7838, 32.0711]).addTo(map.current)
            );
        }
    });

    // { fields.map((field) =>
    //     new mapboxgl.Marker().setLngLat([34.7838, 32.0711]).addTo(map.current)
    // ); }

    

    


    return ( 
        <mapgl className="mapGl">
            {/* <div className="sidebar">
                Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
            </div> */}
            <div ref={mapContainer} className="map-container" />
        </mapgl>
     );
}
 
export default MapGL;
